mapper.select:
select id
from member
where name = #{param1} and phone = #{param2}

mapper.update:
update member
set pw = #{param4}
where id = #{param1} and name = #{param2} and phone = #{param3}

mapper.select:
select count(*) 
from member 
where id = #{param1} and name = #{param2} and phone = #{param3}

select pw
from member
where id = #{param1}
and name = #{param2}
and phone = #{param3}
